import React, {useContext, useEffect, useState} from "react";
import "./Cart.css";
import { ShopContext } from "../Context";
import Cookies from "js-cookie";
import {GrClose} from 'react-icons/gr'






export default function Cart(props){

    const { cart, setCart } = useContext(ShopContext);



    useEffect(() => {
        const data = window.localStorage.getItem('cartItems')
       if( data !== null ) setCart(JSON.parse(data))
    }, [])



    useEffect(() => {
        window.localStorage.setItem('cartItems',JSON.stringify(cart) )

   }, [cart ])    


console.log("cart state",props.cartState);



    return (

        <div className={ props.cartState === true ? "showCartContainer" :  "cartContainer" } >
            <GrClose onClick={() => props.closeCart(props.cartState = false)} />

            <div className="itemsList">
                {cart.map(i => <div className="itemInCart"><img className="cartImage" src={i.api_featured_image} alt="" /><div className="cartTitle"><p className="itemName">{i.name}</p>
                <p className="itemBrand">{i.brand}</p></div>
                <p className="itemPrice">{i.price} $</p></div>)}
            </div>

          <button type="submit">BUY</button>
        </div>
    )
}
