import React, { useContext} from "react";
import { NavLink } from "react-router-dom";
import { ShopContext } from "./Context";


export default function Brands(){

    const {products} = useContext(ShopContext);

    const brand = [];

    for(let i = 0; i < products.length; i++){

        if(!brand.includes(products[i].brand )){

            brand.push(products[i].brand)

        }


    }




    return(

        <ul className="brands">
        {brand.map((i, index) => <li className="brand"><NavLink exact className="categoryLink" activeClassName="activeCategoryLink" to="/Products">{brand[index]}</NavLink></li>)}

    </ul>

    )
}
