import { useContext } from "react";
import React from "react";
import { ShopContext } from "./Context";


export default function ProductCard(){

    const {products, id}  = useContext(ShopContext);


    return (
        <div>
            {products.filter((i) => i.id === id)
        .map((item) => <div className="productContainer">
            <p className="path">{item.category} / {item.brand} / {item.name} </p>
            <img className="productImage" src={item.api_featured_image} alt="" />
            <h2 className="productName">{item.name}</h2>
            <h3 className="brandName">{item.brand}</h3>
            <p className="productPrice">{item.price}</p>
            <p className="productDescription">{item.description}</p>
            <p className="rating">Users Rating {0 + item.rating}/5</p>

            <button className="buyButton" type="submit">Buy Now</button> 
         </div>)}

        </div>
    )

}
