import React, { useContext, useState } from "react";
import { NavLink } from "react-router-dom";
import { ShopContext } from "./Context";
import Categories from "./Categories";
import Brands from "./Brands";
import "../styles/Header.css";

export default function Header() {

document.addEventListener('click', e => {
  const isCategoriesMenu = e.target.matches(".categoryButton")
})



  const { products } = useContext(ShopContext);

  const category = [];






  for (let i = 0; i < products.length; i++) {
    if (!category.includes(products[i].product_type)) {
      category.push(products[i].product_type);
    }
  }






  return (<header>
    <ul className="navBar">

        <li
        className="navbarLink"><NavLink to="/" exact className="homeButton" activeClassName="homeButtonActive">Home</NavLink></li>
      <li
        className="navbarLink"  
      >
        <button className="categoryButton" >Category</button> 
        <Categories  />
      </li>
      <li
        className="navbarLink"
      >

      <button className="brandButton">Brand</button> 
        <Brands  />
      </li>
    </ul>
    </header>
  );
}
