import React, { useContext, useState} from "react";
import { ShopContext } from "./Context";
import Categories from "./Categories";
import Brands from "./Brands";
import "../styles/Header.css"



export default function Header(){

    const [catSubmenu, setCatSubmenu] = useState(0)
    const [brandSubmenu, setBrandSubmenu] = useState(0)



    const {products} = useContext(ShopContext);

    const category = [];

    for(let i = 0; i < products.length; i++){

        if(!category.includes(products[i].product_type)){

            category.push(products[i].product_type)

        }
    }


      const subMenuStyle = {transform: `translateY(${catSubmenu}px)`} 



    return (<ul className="navBar">
       <li className="navbarLink" onMouseOver={() => setCatSubmenu(500)} onMouseOut={() => setCatSubmenu(0)}>Category<Categories styling={subMenuStyle} /></li>
       <li className="navbarLink" onMouseOver={() => setBrandSubmenu(500)} onMouseOut={() => setBrandSubmenu(0)}>Brands<Brands styling={subMenuStyle}/></li>
    </ul>

    )
}
