import React, {useContext, useEffect} from "react";
import "./Cart.css";
import { ShopContext } from "../Context";
import Cookies from "js-cookie";






export default function Cart(props){

    const { cart } = useContext(ShopContext);

    useEffect(() => {
        window.localStorage.setItem('cartItems',JSON.stringify(cart) )

   }, [cart ])    






    return (

        <div className={ !props.cartState ? "cartContainer" : "showCartContainer" } >

            <div className="itemsList">
                {cart.map(i => <div className="itemInCart"><img src={i.api_featured_image} alt="" /><div className="cartTitle"><p className="itemName">{i.name}</p>
                <p className="itemBrand">{i.brand}</p></div>
                <p className="itemPrice">{i.price} $</p></div>)}
            </div>

        </div>
    )
}
