import React, { useContext} from "react";
import { NavLink } from "react-router-dom";
import { ShopContext } from "./Context";


export default function Categories(){

    const {products} = useContext(ShopContext);

    const category = [];

    for(let i = 0; i < products.length; i++){

        if(!category.includes(products[i].product_type)){

            category.push(products[i].product_type)

        }
    }




    return(

        <ul className="categories">
        {category.map((i, index) => <li className="category" categoryProps={category[index]} ><NavLink  exact className="categoryLink" activeClassName="activeCategoryLink" to='/Products' 
         > {category[index].replaceAll('_', ' ')}</NavLink></li>)}

    </ul>

    )
}
