import React, { useContext } from "react";
import { NavLink } from "react-router-dom";
import { ShopContext } from "./Context";

export default function Brands() {
  const { products, productsByBrand } = useContext(ShopContext);

  const brand = [];

  for (let i = 0; i < products.length; i++) {
    if (!brand.includes(products[i].brand)) {
      brand.push(products[i].brand);
    }
  }

  console.log("brandArr", brand);

  return (
    <ul className="brands">
      {brand.map((i) => (
        <li className="brand" onClick={() => productsByBrand(i) }> 
          <NavLink
            exact
            className="brandLink"
            activeClassName="activeBrandLink"
            to="/OneBrand"
          >{i.replaceAll("_", " ")}
          </NavLink>
        </li>
      ))}
    </ul>
  );
}
