import { React, useContext, useEffect } from "react";
import { ShopContext } from "./Context";
import axios from "axios";
import "../styles/App.css";
import Header from "./Header";
import { Route, Switch } from "react-router-dom";
import OneCategory from "./OneCategory";
import OneBrand from "./OneBrand";

function App() {
  const { loadProducts, brand } = useContext(ShopContext);

  useEffect(() => {
    const getData = async () => {
      try {
        const response = await axios.get(
          "http://makeup-api.herokuapp.com/api/v1/products.json"
        );
        console.log("response is", response.data);

        loadProducts(response.data);
      } catch (error) {
        console.log("Get data ERROR:", error.message);
      }
    };

    getData();
  }, []);

  return (
    <div className="App">
      <Header />
     <Switch>

        <Route path="/OneCategory" exact component={OneCategory} />
        <Route path={`/OneBrand/${brand}`} exact component={OneBrand} />
      </Switch>
    </div>
  );
}

export default App;
