import React, { useContext, useState } from "react";
import { NavLink } from "react-router-dom";
import { ShopContext } from "./Context";
import Categories from "./Categories";
import Brands from "./Brands";
import "../styles/Header.css";

export default function Header() {
  const { products } = useContext(ShopContext);

  const [categoriesDrop, setCategoriesDrop] = useState(false)
  const [brandDrop, setBrandDrop] = useState(false)
  const category = [];

  for (let i = 0; i < products.length; i++) {
    if (!category.includes(products[i].product_type)) {
      category.push(products[i].product_type);
    }
  }

  return (
    <header>
      <ul className="navBar">
        <li className="homeLink">
          <NavLink
            to="/"
            exact
            className="homeLink"

          >
            Home
          </NavLink>
        </li>
        <li > <NavLink to="/" className="categoryLink"> 
          Category
          <Categories catDrop={categoriesDrop} /></NavLink> 
        </li>
        <li className="brandLink">
          Brand
          <Brands bDrop={brandDrop} />
        </li>
      </ul>
    </header>
  );
}
